import React from "react";
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function Card({ card, onCardClick }) {

  const currentUser = React.useContext(CurrentUserContext); // ПР11 Подписываемся на контекст CurrentUserContext
  const isOwn = card.owner._id === currentUser._id; // Определяем, являемся ли мы владельцем текущей карточки

const cardDeleteButtonClassName = (
  `card__delete-button ${isOwn ? 'card__delete-button_visible' : 'card__delete-button_hidden'}`
); 
  function handleClick() {
    onCardClick(card);
  }

  return (
    <article className="place">
      <img
        className="place__image"
        alt={card.name}
        src={card.link}
        onClick={handleClick}
      />
      <div className="place__info">
        <h2 className="place__name">{card.name}</h2>
        <div className="place__reaction">
          <button
            type="button"
            className="place__like"
            aria-label="Нравится"
          ></button>
          <span className="place__like-count">{card.likes.length}</span>
        </div>
      </div>
      <button
        type="button"
        className="place__delete"
        arial-label="Удалить"
      ></button>
    </article>
  );
}

export default Card;
