import React, { useState, useContext, useEffect } from "react";
import PopupWithForm from "./PopupWithForm";
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function EditProfilePopup ({isOpen, onClose, onUpdateUser}){
  const currentUser = useContext(CurrentUserContext); // ПР11 Подписываемся на контекст CurrentUserContext
  const [name, setUserName] = useState("");
  const [description, setUserDescription] = useState("");


  function handleNameChange(e) {
    setUserName(e.target.value);
  }

  function handleDescriptionChange(e) {
    setUserDescription(e.target.value);
  }

  function handleSubmit(e) {// Запрещаем браузеру переходить по адресу формы
    e.preventDefault();

    onUpdateUser({ // Передаём значения управляемых компонентов во внешний обработчик
      name,
      about: description,
    });
  }

  useEffect(() => { // После загрузки текущего пользователя из API его данные будут использованы в управляемых компонентах.
    setUserName(currentUser.name);
    setUserDescription(currentUser.about);
  }, [currentUser, isOpen]);

  return (
    <PopupWithForm
        name="profile"
        title="Редактировать профиль"
        id="form-profile"
        formName="edit-profile-form"
        buttonText="Сохранить"
        isOpen={isOpen}
        onClose={onClose}
        onSubmit={handleSubmit}>
            <input
              type="text"
              id="name"
              name="name"
              className="popup__input"
              placeholder="Имя"
              minLength="2"
              maxLength="40"
              required
              defaultValue={name}
              onCHan={handleNameChange} 
            />
            <span 
              id="error-name" 
              className="popup__error">
            </span>
            <input
              type="text"
              id="bio"
              name="bio"
              className="popup__input"
              placeholder="О себе"
              minLength="2"
              maxLength="200"
              required
              defaultValue={description}
              onClose={handleDescriptionChange}
            />
            <span 
              id="error-bio" 
              className="popup__error">
            </span>
        </PopupWithForm>
  )
}

export default EditProfilePopup;
