import React, {useContext, useState } from "react";
import PopupWithForm from "./PopupWithForm";
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function EditAvatarPopup ({isOpen, onClose }){
  const currentUser = useContext(CurrentUserContext); // ПР11 Подписываемся на контекст CurrentUserContext
  const [avatar, setAvatar] = useState("");

  function handleAvatarChange(e) {
    setAvatar(e.target.value);
  }
  function handleSubmit(e) {
    e.preventDefault();
    onUpdateAvatar({
      avatar,
    });
  } 

  return (
  <PopupWithForm
        name="profile-avatar"
        isOpen={isOpen}
        title="Обновить автар"
        id="form-profile-avatar"
        formName="edit-profile-avatar"
        buttonText="Сохранить"
        onClose={onClose}
        onSubmit={handleSubmit}>
            <input
              type="url"
              id="profile-avatar-link"
              name="profile-avatar-link"
              className="popup__input popup__input_profile-avatar_link"
              placeholder="Ссылка на картинку"
              required
            />
            <span
              id="error-profile-avatar-link"
              className="popup__error">
            </span>
    </PopupWithForm>
  )
}

export default EditAvatarPopup;
